mapper.select:
select b.*
from (
    select rownum rn, a.*
    from (
        select *
        from tbl_board
        order by board_no desc
        ) a
    ) b
where b.rn > (#{page} - 1 ) * 5

	and b.rn <= (#{page} * 5 )

mapper.update:
update tbl_board
set update_date = sysdate

	,title = #{title}


	,content = #{content}

where board_no = #{boardNo}

update tbl_board
set view_cnt = view_cnt + 1
where board_no = #{boardNo}
